import * as echarts from 'echarts';
export default {
  name: 'Home',
  data() {
    return {
      user: JSON.parse(localStorage.getItem('xm-user') || '{}')
    };
  },
  mounted() {
    const pieChart = echarts.init(document.getElementById('pie')); // 图表一：商品销售额柱状图
    const pictorialChart = echarts.init(document.getElementById('pictorial')); // 图表二：商家销售额象形图（降序）
    const categoryPieChart = echarts.init(document.getElementById('categoryPie')); // 图表三：商品种类数量饼图

    // ✅ 图表一：商品销售额柱状图
    this.$request.get('/orders/selectLine').then(res => {
      const data = res.data || [];
      const barOption = {
        title: {
          text: '商品销售额对比',
          subtext: '柱状图',
          left: 'center'
        },
        tooltip: {
          trigger: 'axis',
          axisPointer: {
            type: 'shadow'
          }
        },
        xAxis: {
          type: 'category',
          data: data.map(v => v.name),
          axisLabel: {
            rotate: 30,
            interval: 0
          }
        },
        yAxis: {
          type: 'value',
          name: '销售额（元）'
        },
        series: [{
          name: '销售额',
          type: 'bar',
          barWidth: '60%',
          data: data.map(v => v.value),
          itemStyle: {
            color: '#fac858'
          }
        }]
      };
      pieChart.setOption(barOption);
    });

    // ✅ 图表二：商家销售额象形图（按销售额降序排序）
    this.$request.get('/orders/selectBar').then(res => {
      const data = (res.data || []).sort((a, b) => b.value - a.value);
      const pictorialOption = {
        title: {
          text: '商家销售额排行榜',
          subtext: '象形柱状图（降序）',
          left: 'center'
        },
        tooltip: {
          trigger: 'axis',
          axisPointer: {
            type: 'shadow'
          }
        },
        xAxis: {
          type: 'category',
          data: data.map(v => v.name),
          axisTick: {
            alignWithLabel: true
          }
        },
        yAxis: {
          type: 'value',
          name: '销售额（元）'
        },
        series: [{
          name: '销售额',
          type: 'pictorialBar',
          symbol: 'rect',
          symbolSize: ['50%', '80%'],
          symbolRepeat: true,
          symbolMargin: 2,
          data: data.map(v => v.value),
          itemStyle: {
            color: '#5B8FF9'
          }
        }]
      };
      pictorialChart.setOption(pictorialOption);
    });

    // ✅ 图表三：商品种类销售数量占比（饼图）
    this.$request.get('/orders/selectCategorySalesCount').then(res => {
      const data = res.data || [];
      const categoryOption = {
        title: {
          text: '商品种类销量占比',
          subtext: '饼图',
          left: 'center'
        },
        tooltip: {
          trigger: 'item'
        },
        legend: {
          orient: 'vertical',
          left: 'left'
        },
        series: [{
          name: '销量',
          type: 'pie',
          radius: '60%',
          center: ['50%', '55%'],
          data: data.map(item => ({
            value: item.value,
            name: item.name
          })),
          emphasis: {
            itemStyle: {
              shadowBlur: 10,
              shadowOffsetX: 0,
              shadowColor: 'rgba(0, 0, 0, 0.5)'
            }
          }
        }]
      };
      categoryPieChart.setOption(categoryOption);
    });
  }
};
