import * as echarts from 'echarts';
export default {
  name: 'Home',
  data() {
    return {
      user: JSON.parse(localStorage.getItem('xm-user') || '{}'),
      todayTotal: 0,
      // 今日销售额
      todayCount: 0,
      // 今日订单数
      dateRange: [] // 时间区间
    };
  },

  methods: {
    onDateChange() {
      this.loadAllCharts();
    },
    loadAllCharts() {
      const [startDate, endDate] = this.dateRange || [];
      const pieChart = echarts.init(document.getElementById('pie'));
      const pictorialChart = echarts.init(document.getElementById('pictorial'));
      const categoryPieChart = echarts.init(document.getElementById('categoryPie'));
      const onlineTrendChart = echarts.init(document.getElementById('onlineTrendChart'));
      // 图1
      this.$request.get('/orders/lineWithCount', {
        params: {
          startDate,
          endDate
        }
      }).then(res => {
        const data = res.data || [];
        const lineOption = {
          title: {
            text: '近七日销售额 & 订单数趋势',
            left: 'center',
            top: 10
          },
          tooltip: {
            trigger: 'axis'
          },
          legend: {
            data: ['销售额', '订单数'],
            top: 50
          },
          grid: {
            top: 90,
            left: 60,
            right: 60,
            bottom: 60
          },
          xAxis: {
            type: 'category',
            data: data.map(v => v.name),
            axisLabel: {
              rotate: 30
            }
          },
          yAxis: [{
            type: 'value',
            name: '销售额（元）',
            axisLine: {
              lineStyle: {
                color: '#5470c6'
              }
            }
          }, {
            type: 'value',
            name: '订单数（单）',
            minInterval: 1,
            axisLine: {
              lineStyle: {
                color: '#91cc75'
              }
            }
          }],
          series: [{
            name: '销售额',
            type: 'line',
            smooth: true,
            yAxisIndex: 0,
            data: data.map(v => v.sales),
            itemStyle: {
              color: '#5470c6'
            }
          }, {
            name: '订单数',
            type: 'line',
            smooth: true,
            yAxisIndex: 1,
            data: data.map(v => v.count),
            itemStyle: {
              color: '#91cc75'
            }
          }]
        };
        pieChart.setOption(lineOption);
      });
      // 图2
      this.$request.get('/orders/selectBar', {
        params: {
          startDate,
          endDate
        }
      }).then(res => {
        const data = (res.data || []).sort((a, b) => b.value - a.value);
        const horizontalBarOption = {
          title: {
            text: '商家销售额排行榜',
            left: 'center'
          },
          tooltip: {
            trigger: 'axis',
            axisPointer: {
              type: 'shadow'
            }
          },
          grid: {
            left: '120px',
            right: '40px',
            bottom: '50px',
            top: '80px'
          },
          xAxis: {
            type: 'value',
            name: '销售额（元）'
          },
          yAxis: {
            type: 'category',
            data: data.map(v => v.name),
            inverse: true,
            axisLabel: {
              fontSize: 12
            }
          },
          series: [{
            name: '销售额',
            type: 'bar',
            barWidth: '50%',
            data: data.map(v => v.value),
            itemStyle: {
              color: '#91cc75'
            },
            label: {
              show: true,
              position: 'right'
            }
          }]
        };
        pictorialChart.setOption(horizontalBarOption);
      });
      // 图3
      this.$request.get('/orders/selectCategorySalesCount', {
        params: {
          startDate,
          endDate
        }
      }).then(res => {
        const data = res.data || [];
        const categoryOption = {
          title: {
            text: '商品种类销量占比',
            left: 'center'
          },
          tooltip: {
            trigger: 'item'
          },
          legend: {
            orient: 'vertical',
            left: 'left'
          },
          series: [{
            name: '销量',
            type: 'pie',
            radius: '60%',
            center: ['50%', '55%'],
            data: data.map(item => ({
              value: item.value,
              name: item.name
            })),
            emphasis: {
              itemStyle: {
                shadowBlur: 10,
                shadowOffsetX: 0,
                shadowColor: 'rgba(0, 0, 0, 0.5)'
              }
            }
          }]
        };
        categoryPieChart.setOption(categoryOption);
      });
      // 图4
      this.$request.get('/user/selectOnlineTrend', {
        params: {
          startDate,
          endDate
        }
      }).then(res => {
        const data = res.data || [];
        const onlineOption = {
          title: {
            text: '近七日用户在线趋势',
            left: 'center'
          },
          tooltip: {
            trigger: 'axis'
          },
          xAxis: {
            type: 'category',
            data: data.map(v => v.name),
            axisLabel: {
              rotate: 30,
              interval: 0
            }
          },
          yAxis: {
            type: 'value',
            name: '在线用户数',
            minInterval: 1
          },
          series: [{
            name: '在线人数',
            type: 'line',
            smooth: true,
            data: data.map(v => v.value),
            itemStyle: {
              color: '#73c0de'
            }
          }]
        };
        onlineTrendChart.setOption(onlineOption);
      });
    }
  },
  mounted() {
    this.loadAllCharts();
    // 保持今日销售额、订单数原有逻辑
    this.$request.get('/orders/todayTotal').then(res => {
      this.todayTotal = res.data || 0;
    });
    this.$request.get('/orders/todayCount').then(res => {
      this.todayCount = res.data || 0;
    });
  }
};
