import * as echarts from 'echarts';
export default {
  name: 'Home',
  data() {
    return {
      user: JSON.parse(localStorage.getItem('xm-user') || '{}')
    };
  },
  mounted() {
    const pieChart = echarts.init(document.getElementById('pie'));
    const pictorialChart = echarts.init(document.getElementById('pictorial'));
    this.$request.get('/orders/selectLine').then(res => {
      const data = res.data || [];
      const pieData = data.map(v => ({
        name: v.name,
        value: v.value
      }));
      const pieOption = {
        title: {
          text: '近八日商品销售额占比',
          subtext: '饼图',
          left: 'center'
        },
        tooltip: {
          trigger: 'item'
        },
        legend: {
          orient: 'vertical',
          left: 'left'
        },
        series: [{
          name: '销售额',
          type: 'pie',
          radius: '60%',
          data: pieData,
          emphasis: {
            itemStyle: {
              shadowBlur: 10,
              shadowOffsetX: 0,
              shadowColor: 'rgba(0, 0, 0, 0.5)'
            }
          }
        }]
      };
      pieChart.setOption(pieOption);
    });
    this.$request.get('/orders/selectBar').then(res => {
      const data = res.data || [];
      const pictorialOption = {
        title: {
          text: '商家销售额排行榜',
          subtext: '象形柱状图',
          left: 'center'
        },
        tooltip: {
          trigger: 'axis',
          axisPointer: {
            type: 'shadow'
          }
        },
        xAxis: {
          type: 'category',
          data: data.map(v => v.name),
          axisTick: {
            alignWithLabel: true
          }
        },
        yAxis: {
          type: 'value'
        },
        series: [{
          name: '销售额',
          type: 'pictorialBar',
          symbol: 'rect',
          symbolSize: ['50%', '80%'],
          symbolRepeat: true,
          symbolMargin: 2,
          data: data.map(v => v.value),
          itemStyle: {
            color: '#5B8FF9'
          }
        }]
      };
      pictorialChart.setOption(pictorialOption);
    });
  }
};
